	(24323, 0, 0, 'Show the abberations no mercy, Ymiron!', 14, 0, 100, 1, 0, 0, 0, 23125, 0, 'Ancient Citizen of Nifflevar yell'),
	(24323, 1, 0, 'They weaken us! Our strength is dilluted by their very existence! Destroy them all!', 14, 0, 100, 113, 0, 0, 0, 23129, 0, 'Ancient Citizen of Nifflevar yell'),
	(24327, 0, 0, 'Shall we prepare it for you, my lord?', 12, 0, 100, 0, 0, 0, 0, 23152, 0, 'Val\'kyr Soulclaimer'),
