	(24706, 1, 0, 'I\'ll die with my axes in my hands and drenched in the blood of our foes!', 12, 0, 100, 53, 0, 0, 0, 24793, 0, 'Durm Icehide'),
	(24706, 2, 0, 'A dishonorable scheme.', 12, 0, 100, 274, 0, 0, 0, 24801, 0, 'Durm Icehide'),
