	(25162, 0, 0, 'Attention!', 12, 0, 100, 0, 0, 0, 0, 26245, 0, 'Drill Sergeant Bahduum'),
	(25162, 1, 0, 'Alright, quiet down. We have a lot to do and very little time to do it in, people.', 12, 0, 100, 0, 0, 0, 0, 24210, 0, 'Drill Sergeant Bahduum'),
