	(27390, 4, 0, 'Pfagh! What sort of necromancer lets death stop him? I knew you were worthless!', 14, 0, 100, 0, 0, 13233, 0, 29533, 0, 'skarvald YELL_SKARVALD_DAL_DIEDFIRST'),
	(27405, 0, 0, 'What\'s going on here, sarge?', 12, 0, 100, 0, 0, 0, 0, 26541, 0, 'Onslaught Footman'),
	(27406, 0, 0, 'I don\'t know. Better kill them both to be on the safe side!', 12, 0, 100, 0, 0, 0, 0, 26542, 0, 'Onslaught Footman'),
	(27411, 0, 0, 'Were you bitten?', 12, 7, 100, 6, 0, 0, 0, 26640, 0, 'Freed Alliance Scout Say1'),
	(27411, 1, 1, 'Answer me!  Were you bloody bitten?', 14, 7, 100, 22, 0, 0, 0, 26641, 0, 'Freed Alliance Scout Yell'),
	(27411, 2, 2, 'You don\'t even know, do you?  The trappers?  They\'re not human... we have to get out of here!  Come, quick!', 12, 7, 100, 0, 0, 0, 0, 26539, 0, 'Freed Alliance Scout Say2'),
	(27411, 3, 3, 'Take Ducal\'s horse, he\'s one of them now!', 12, 7, 100, 0, 0, 0, 0, 26540, 0, 'Freed Alliance Scout Say3'),
