	(28610, 0, 2, 'I\'m through being courteous with your kind, human! What is the "Crimson Dawn?"', 12, 0, 100, 0, 0, 0, 0, 29135, 0, 'Scarlet Marksman'),
	(28610, 0, 3, '"Crimson Dawn!" What is it! Speak!', 12, 0, 100, 0, 0, 0, 0, 29136, 0, 'Scarlet Marksman'),
