	(28923, 2, 0, 'What hope is there for you? None!', 14, 0, 100, 0, 0, 14162, 0, 30535, 0, 'Loken'),
	(28923, 3, 0, 'You cannot hide from fate!', 14, 0, 100, 0, 0, 14163, 0, 30538, 0, 'Loken'),
	(28923, 3, 1, 'Come closer. I will make it quick.', 14, 0, 100, 0, 0, 14164, 0, 30539, 0, 'Loken'),
	(28923, 3, 2, 'Your flesh cannot hold out for long.', 14, 0, 100, 0, 0, 14165, 0, 30540, 0, 'Loken'),
	(28923, 4, 0, 'Only mortal...', 14, 0, 100, 0, 0, 14166, 0, 30541, 0, 'Loken'),
	(28923, 4, 1, 'I... am... FOREVER!', 14, 0, 100, 0, 0, 14167, 0, 30542, 0, 'Loken'),
	(28923, 4, 2, 'What little time you had, you wasted!', 14, 0, 100, 0, 0, 14168, 0, 30543, 0, 'Loken'),
	(28923, 5, 0, 'You stare blindly into the abyss!', 14, 0, 100, 0, 0, 14169, 0, 30545, 0, 'Loken'),
	(28923, 6, 0, 'Your ignorance is profound. Can you not see where this path leads?', 14, 0, 100, 0, 0, 14170, 0, 30546, 0, 'Loken'),
	(28923, 7, 0, 'You cross the precipice of oblivion!', 14, 0, 100, 0, 0, 14171, 0, 30547, 0, 'Loken'),
	(28923, 8, 0, 'My death... heralds the end of this world.', 14, 0, 100, 0, 0, 14172, 0, 30544, 0, 'Loken'),
	(28923, 9, 0, '%s begins to cast Lightning Nova!', 41, 0, 100, 0, 0, 0, 0, 29865, 0, 'Loken'),
