	(29173, 3, 0, 'Soldiers of the Scourge, death knights of Acherus, minions of the darkness: hear the call of the Highlord!', 14, 0, 100, 0, 0, 14679, 0, 29699, 0, 'Highlord Darion Mograine'),
	(29173, 4, 0, 'RISE!', 14, 0, 100, 0, 0, 14680, 0, 31198, 0, 'Highlord Darion Mograine'),
	(29173, 5, 0, 'The skies turn red with the blood of the fallen! The Lich King watches over us, minions! Leave only ashes and misery in your destructive wake!', 14, 0, 100, 0, 0, 14682, 0, 29722, 0, 'Highlord Darion Mograine'),
	(29173, 8, 0, 'Kneel before the Highlord!', 12, 0, 100, 0, 0, 14683, 0, 29727, 0, 'Highlord Darion Mograine'),
	(29173, 8, 1, 'You stand no chance!', 12, 0, 100, 0, 0, 14684, 0, 29728, 0, 'Highlord Darion Mograine'),
	(29173, 8, 2, 'The Scourge will destroy this place!', 12, 0, 100, 0, 0, 14685, 0, 29729, 0, 'Highlord Darion Mograine'),
	(29173, 8, 3, 'Your life is forfeit.', 12, 0, 100, 0, 0, 14686, 0, 62815, 0, 'Highlord Darion Mograine'),
	(29173, 8, 4, 'Life is meaningless without suffering!', 12, 0, 100, 0, 0, 14687, 0, 29731, 0, 'Highlord Darion Mograine'),
