	(29175, 24, 0, 'You cannot win, Darion!', 14, 0, 100, 0, 0, 14584, 0, 29743, 0, 'Highlord Tirion Fordring'),
	(29175, 25, 0, 'Bring them before the chapel!', 14, 0, 100, 0, 0, 14585, 0, 29756, 0, 'Highlord Tirion Fordring'),
