	(37120, 6, 0, 'Father... For you, I would give my life a thousand times.', 12, 0, 100, 396, 0, 17413, 0, 38151, 0, 'Highlord Darion Mograine'),
	(37122, 0, 0, 'Never... could reach... the top shelf...', 14, 0, 0, 0, 0, 16586, 0, 37074, 0, 'Captain Arnath - SAY_ARNATH_DEATH'),
	(37122, 1, 0, 'You miserable fools never did manage to select a decent bat wing.', 14, 0, 0, 0, 0, 16587, 0, 37076, 0, 'Captain Arnath - SAY_ARNATH_RESURRECTED'),
