	(37125, 0, 0, 'It was... a worthy afterlife.', 14, 0, 0, 0, 0, 16998, 0, 37668, 0, 'Captain Rupert - SAY_RUPERT_DEATH'),
	(37125, 1, 0, 'There is no escaping the Lich King\'s will. Prepare for an explosive encounter!', 14, 0, 0, 0, 0, 16999, 0, 37669, 0, 'Captain Rupert - SAY_RUPERT_RESURRECTED'),
