	(13020, 1, 0, 0, 0, 0, 0, 0, 0, 'It is good to know the people of this land still pay homage to the olden races.  I bid you well, $N, and offer you this token...', 15050),
	(13021, 1, 0, 0, 0, 0, 0, 0, 0, 'It is good to know the people of this land still pay homage to the olden races.  I bid you well, $N, and offer you this token...', 15050),
	(13024, 1, 0, 0, 0, 0, 0, 0, 0, 'Your spirit burns with life, young $c.  I accept the homage you pay, and offer in return this token...', 15050),
	(13025, 1, 0, 0, 0, 0, 0, 0, 0, 'Your spirit burns with life, young $c.  I accept the homage you pay, and offer in return this token...', 15050),
	(13026, 1, 0, 0, 0, 0, 0, 0, 0, 'Your spirit burns with life, young $c.  I accept the homage you pay, and offer in return this token...', 15050),
